    def insert_account(self):
        try:
            admin_username = get_admin_account()
            admin_password = get_admin_password()
            if log_status() is False:
                QMessageBox.critical(self.pushButton_addAccount, "错误", "请先登录！")
                self.label_actionStatus.setText("请先登录！")
                self.label_actionStatus.setStyleSheet("color: rgb(255, 0, 0);")
                return

            mydatabase = mc.connect(
                host="localhost",
                user="{}".format(admin_username),
                password="{}".format(admin_password),
                database="library"
            )

            tablename = "tbl_addaccount"

            name = self.lineEdit_accountName.text()
            accountID = self.lineEdit_accountID.text()
            phone = self.lineEdit_accountPhone.text()
            email = self.lineEdit_accountEmail.text()

            # 如果有一个为空，则不插入
            if name == "" or accountID == "" or phone == "" or email == "":
                QMessageBox.critical(self.pushButton_addAccount, "错误", "请填写完整信息！")
                self.label_actionStatus.setText("请填写完整信息！")
                self.label_actionStatus.setStyleSheet("color: rgb(200, 150, 0);")
                return

            mycursor = mydatabase.cursor()
            sql = "INSERT INTO {} (name, accountID, phone, email) VALUES (%s, %s, %s, %s)".format(tablename)
            val = (name, accountID, phone, email)
            mycursor.execute(sql, val)
            mydatabase.commit()
            print(mycursor.rowcount, "记录插入成功。")

            mycursor.close()
            mydatabase.close()
            QMessageBox.information(self.pushButton_addAccount, "提示", "记录插入成功！")
            self.label_actionStatus.setText("记录插入成功！")
            self.label_actionStatus.setStyleSheet("color: rgb(0, 200, 0);")
            # 清空输入框
            self.lineEdit_accountName.setText("")
            self.lineEdit_accountID.setText("")
            self.lineEdit_accountPhone.setText("")
            self.lineEdit_accountEmail.setText("")

        except mc.Error as e:
            print("记录添加失败：", e)
            QMessageBox.critical(self.pushButton_addAccount, "错误", "记录添加失败！")
            # 输出 e.args 的内容
            self.label_actionStatus.setText(e.args[1])
            self.label_actionStatus.setStyleSheet("color: rgb(200, 0, 0);")

